def print_hi(name):
    # Use a breakpoint in the code line below to debug your script.
    print(f'Hi, {name}')  # Press Ctrl+F8 to toggle the breakpoint.


# Press the green button in the gutter to run the script.
if __name__ == '__main__':
    print_hi('PyCharm')

    input = ''

    le = len(input)
    count = 0
    tmpdict = {}
    while count < le-4:
        tmp = input[count:count+4]
        for char in tmp:
            tmpdict[str(char)] = 0
        if len(tmpdict) <4:
            count = count+1
            tmpdict = {}
        else:
            marker = count+4
            break

    while count < le-14:
        tmp = input[count:count+14]
        for char in tmp:
            tmpdict[str(char)] = 0
        if len(tmpdict) <14:
            count = count+1
            tmpdict = {}
        else:
            marker2 = count+14
            break

    tmpstr = input[marker-4:marker]
    tmpstr2 = input[marker2-14:marker2]

    print("packet ID: " + tmpstr)
    print(str(marker))
    print("Message: " + tmpstr2)
    print(str(marker2))






